data = []

def datenormalisation(date):
    datels = date.split('-')
    day_nrmlsed = 1 if int(datels[2][:3]) < 16 else 2
    mth = int(datels[1])
    nmrlsed = day_nrmlsed*mth
    #nmrlsed_scr = (avg/year_occr[datels[2][:4]])
    return nmrlsed


def yrMTPY(year):
    yearsSince2019 = 33 - (int(year)-1986)
    return 1/(2**yearsSince2019)


finalData = []
with open('eBirdClean_Before2020.csv', mode='r', encoding='utf-8') as csv_file:
    csv_reader = csv.reader(csv_file, delimiter=',')

    for rowCSV in csv_reader:
        data.append(rowCSV)

for birdls in birdy:
    bird = birdls[0].strip().lower()
    bird = str(bird)
    curYear = 0

    pred = 0

    for row in data:
        check = row[2].strip().lower()
        check = str(check)
        if check == bird:
            try:
                # single occurence per year
                if curYear != row[8].split('-')[0]:
                    # first period of november
                    if datenormalisation(row[8]) == 20:
                        year = row[8].split('-')[0]
                        yearMultiplier = yrMTPY(year)
                        if pred + yearMultiplier < 1:
                            pred += yearMultiplier
                            curYear = year
            except:
                continue

    finalData.append([birdls[0], birdls[1], birdls[2], pred])

print(finalData)

